diff --git a/BitPantry.CommandLine.Remote.SignalR.Client/Profiles/ProfileManager.cs b/BitPantry.CommandLine.Remote.SignalR.Client/Profiles/ProfileManager.cs
index 1624a1f..a15d528 100644
--- a/BitPantry.CommandLine.Remote.SignalR.Client/Profiles/ProfileManager.cs
+++ b/BitPantry.CommandLine.Remote.SignalR.Client/Profiles/ProfileManager.cs
@@ -67,9 +67,21 @@ public class ProfileManager : IProfileManager
         if (string.IsNullOrWhiteSpace(profile.Name))
             throw new ArgumentException("Profile name cannot be empty", nameof(profile));

+        // Validate profile name length (max 64 characters)
+        if (profile.Name.Length > 64)
+            throw new ArgumentException("Profile name cannot exceed 64 characters", nameof(profile));
+        
+        // Validate profile name doesn't start with hyphen
+        if (profile.Name.StartsWith('-'))
+            throw new ArgumentException("Profile name cannot start with a hyphen", nameof(profile));
+        
         // Validate profile name contains only allowed characters (letters, digits, hyphens, underscores)
         if (!IsValidProfileName(profile.Name))
             throw new ArgumentException("Profile name contains invalid characters. Only letters, digits, hyphens, and underscores are allowed.", nameof(profile));
+        
+        // Validate URI is well-formed
+        if (string.IsNullOrWhiteSpace(profile.Uri) || !Uri.TryCreate(profile.Uri, UriKind.Absolute, out _))
+            throw new ArgumentException("Profile URI is invalid or malformed", nameof(profile));

         var config = await LoadConfigurationAsync(ct);

@@ -193,21 +205,29 @@ public class ProfileManager : IProfileManager
         if (!_fileSystem.File.Exists(_configFilePath))
             return new ProfileConfiguration();

-        var json = await _fileSystem.File.ReadAllTextAsync(_configFilePath, ct);
-        var config = JsonSerializer.Deserialize<ProfileConfiguration>(json, _jsonOptions) ?? new ProfileConfiguration();
-        
-        // Ensure dictionary uses case-insensitive comparison (JSON deserialization loses the custom comparer)
-        if (config.Profiles.Count > 0)
+        try
         {
-            var profiles = new Dictionary<string, ServerProfile>(StringComparer.OrdinalIgnoreCase);
-            foreach (var kvp in config.Profiles)
+            var json = await _fileSystem.File.ReadAllTextAsync(_configFilePath, ct);
+            var config = JsonSerializer.Deserialize<ProfileConfiguration>(json, _jsonOptions) ?? new ProfileConfiguration();
+            
+            // Ensure dictionary uses case-insensitive comparison (JSON deserialization loses the custom comparer)
+            if (config.Profiles.Count > 0)
             {
-                profiles[kvp.Key] = kvp.Value;
+                var profiles = new Dictionary<string, ServerProfile>(StringComparer.OrdinalIgnoreCase);
+                foreach (var kvp in config.Profiles)
+                {
+                    profiles[kvp.Key] = kvp.Value;
+                }
+                config.Profiles = profiles;
             }
-            config.Profiles = profiles;
+            
+            return config;
+        }
+        catch (JsonException)
+        {
+            // Corrupted file - return empty configuration (will be overwritten on next save)
+            return new ProfileConfiguration();
         }
-        
-        return config;
     }

     private async Task SaveConfigurationAsync(ProfileConfiguration config, CancellationToken ct)
diff --git a/BitPantry.CommandLine.Tests.Remote.SignalR/ClientTests/ProfileManagerTests.cs b/BitPantry.CommandLine.Tests.Remote.SignalR/ClientTests/ProfileManagerTests.cs
index 25d4adb..80d505a 100644
--- a/BitPantry.CommandLine.Tests.Remote.SignalR/ClientTests/ProfileManagerTests.cs
+++ b/BitPantry.CommandLine.Tests.Remote.SignalR/ClientTests/ProfileManagerTests.cs
@@ -395,6 +395,125 @@ namespace BitPantry.CommandLine.Tests.Remote.SignalR.ClientTests
                 .WithMessage("*invalid*character*");
         }

+        [TestMethod]
+        public async Task CreateProfile_TooLongName_ThrowsValidation()
+        {
+            // Arrange
+            var profileManager = new ProfileManager(_fileSystem, _storagePath, _credentialStore);
+            var longName = new string('a', 65); // 65 chars exceeds 64 char limit
+            var profile = new ServerProfile { Name = longName, Uri = "https://example.com" };
+
+            // Act
+            Func<Task> act = async () => await profileManager.CreateProfileAsync(profile);
+
+            // Assert
+            await act.Should().ThrowAsync<ArgumentException>()
+                .WithMessage("*64*characters*");
+        }
+
+        [TestMethod]
+        public async Task CreateProfile_HyphenInName_Succeeds()
+        {
+            // Arrange
+            var profileManager = new ProfileManager(_fileSystem, _storagePath, _credentialStore);
+            var profile = new ServerProfile { Name = "my-profile", Uri = "https://example.com" };
+
+            // Act
+            await profileManager.CreateProfileAsync(profile);
+
+            // Assert
+            var retrieved = await profileManager.GetProfileAsync("my-profile");
+            retrieved.Should().NotBeNull("hyphen in middle of name should be allowed");
+            retrieved!.Name.Should().Be("my-profile");
+        }
+
+        [TestMethod]
+        public async Task CreateProfile_StartsWithHyphen_ThrowsValidation()
+        {
+            // Arrange
+            var profileManager = new ProfileManager(_fileSystem, _storagePath, _credentialStore);
+            var profile = new ServerProfile { Name = "-profile", Uri = "https://example.com" };
+
+            // Act
+            Func<Task> act = async () => await profileManager.CreateProfileAsync(profile);
+
+            // Assert
+            await act.Should().ThrowAsync<ArgumentException>()
+                .WithMessage("*cannot start with*hyphen*");
+        }
+
+        [TestMethod]
+        public async Task CreateProfile_InvalidUri_ThrowsValidation()
+        {
+            // Arrange
+            var profileManager = new ProfileManager(_fileSystem, _storagePath, _credentialStore);
+            var profile = new ServerProfile { Name = "production", Uri = "not-a-valid-uri" };
+
+            // Act
+            Func<Task> act = async () => await profileManager.CreateProfileAsync(profile);
+
+            // Assert
+            await act.Should().ThrowAsync<ArgumentException>()
+                .WithMessage("*URI*invalid*");
+        }
+
+        #endregion
+
+        #region Edge Case Tests
+
+        [TestMethod]
+        public async Task CreateProfile_CorruptedFile_RecreatesFile()
+        {
+            // Arrange - Create corrupted profiles.json
+            var configPath = _fileSystem.Path.Combine(_storagePath, "profiles.json");
+            _fileSystem.File.WriteAllText(configPath, "{ this is not valid json }");
+            var profileManager = new ProfileManager(_fileSystem, _storagePath, _credentialStore);
+
+            // Act - Should recreate file and add profile successfully
+            var profile = new ServerProfile { Name = "production", Uri = "https://example.com" };
+            await profileManager.CreateProfileAsync(profile);
+
+            // Assert
+            var retrieved = await profileManager.GetProfileAsync("production");
+            retrieved.Should().NotBeNull("corrupted file should be recreated to allow new profile");
+        }
+
+        [TestMethod]
+        public async Task GetAllProfiles_MissingFile_ReturnsEmpty()
+        {
+            // Arrange - Ensure no profiles.json exists
+            var configPath = _fileSystem.Path.Combine(_storagePath, "profiles.json");
+            if (_fileSystem.File.Exists(configPath))
+                _fileSystem.File.Delete(configPath);
+            var profileManager = new ProfileManager(_fileSystem, _storagePath, _credentialStore);
+
+            // Act
+            var profiles = await profileManager.GetAllProfilesAsync();
+
+            // Assert
+            profiles.Should().BeEmpty("missing file should return empty list, not throw");
+        }
+
+        [TestMethod]
+        public async Task CreateProfile_DirectoryNotExists_CreatesDirectory()
+        {
+            // Arrange - Use a path where directory doesn't exist
+            var newPath = @"C:\Users\TestUser\.bitpantry\commandline\newprofiles";
+            // Ensure directory doesn't exist
+            if (_fileSystem.Directory.Exists(newPath))
+                _fileSystem.Directory.Delete(newPath, true);
+            var profileManager = new ProfileManager(_fileSystem, newPath, _credentialStore);
+
+            // Act
+            var profile = new ServerProfile { Name = "production", Uri = "https://example.com" };
+            await profileManager.CreateProfileAsync(profile);
+
+            // Assert
+            _fileSystem.Directory.Exists(newPath).Should().BeTrue("directory should be created");
+            var retrieved = await profileManager.GetProfileAsync("production");
+            retrieved.Should().NotBeNull("profile should be saved in newly created directory");
+        }
+
         #endregion
     }
 }
